import useInput from "../hooks/useInput";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router";
import { useEffect } from "react";
import { getOneProduct, modifyProduct } from "../store/products";

const EditCourse = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const productName = useInput();
  const price = useInput();
  const category = useInput();
  const overview = useInput();
  const image = useInput();
  const duration = useInput();
  const requirements = useInput();

  console.log("ID", id)

  const course = useSelector(state => state.products)

  useEffect(() => {
    dispatch(getOneProduct(id));
  }, [])

  console.log("cursooooo --> ", course);


  const handlerSubmit = (e) => {
    e.preventDefault();
    dispatch(
      modifyProduct({
        productName: productName.value,
        price: price.value,
        category: category.value,
        overview: overview.value,
        ranking: null,
        image: image.value,
        duration: duration.value,
        requirements: requirements.value,
        students: 0
      })
    )
      .then((course) => {
        console.log("curso updated -->", course)
      }) //ver ruta
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <h1>editar curso: {course.productName}</h1>

      <form onSubmit={handlerSubmit}>
        <label>Course</label>
        <input
          {...productName}
          type="text"
          placeholder={course.productName}
          required
        />
        <label>Price</label>
        <input {...price} type="text" placeholder={course.price} required />
        <label>Categoria</label>
        <input
          {...category}
          type="text"
          placeholder={course}
          required
        />
        <label>About</label>
        <input
          {...overview}
          type="text"
          placeholder="Breve descripción del curso"
          required
        />
        <label>Image</label>
        <input {...image} type="text" placeholder="img.jpg" required />
        <label>Duration</label>
        <input {...duration} type="text" placeholder="4 semanas" required />
        <label>Requirements</label>
        <input
          {...requirements}
          type="text"
          placeholder="Equipo, software y otros materiales requeridos"
          required
        />
        <button>EDIT COURSE</button>
      </form>




    </div>
  );


};


export default EditCourse;
