            <input
              {...category}
              type="text"
              placeholder={course.category}
              required
            /></li>
          <li><label>About</label>
            <input
              {...overview}
              type="text"
              placeholder={course.overview}
              required
            /></li>
          <li><label>Image</label>
            <input {...image} type="text" placeholder={course.image} required /></li>
          <li><label>Duration</label>
            <input {...duration} type="text" placeholder={course.duration} required /></li>
          <li> <label>Requirements</label>
            <input
              {...requirements}
              type="text"
              placeholder={course.requirements}
              required
            /></li>
          <button>UPDATE</button>
