import useInput from "../hooks/useInput";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router";
import { useEffect } from "react";
import { getOneProduct, modifyProduct } from "../store/singleProduct";

const EditCourse = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const productName = useInput();
  const price = useInput();
  const category = useInput();
  const overview = useInput();
  const image = useInput();
  const duration = useInput();
  const requirements = useInput();
  const navigate = useNavigate();

  useEffect(() => {
    dispatch(getOneProduct(id));
  }, [])

  const course = useSelector(state => state.product)


  const handlerSubmit = (e) => {
    e.preventDefault();
    dispatch(
      modifyProduct({
        id: id,
        productName: productName.value,
        price: price.value,
        category: category.value,
        overview: overview.value,
        image: image.value,
        duration: duration.value,
        requirements: requirements.value,
      })
    )
      .then((course) => {
        navigate("/admin/products")
      }) 
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <h1>editar curso: {course.productName}</h1>

      <form onSubmit={handlerSubmit}>
       <ul>
       <li> <label>Course</label>
        <input
          {...productName}
          type="text"
          placeholder={course.productName}
          required
        /> </li>
        <li><label>Price</label>
        <input {...price} type="text" placeholder={course.price} required></input></li>
        <li><label>Categoria</label>
        <input
          {...category}
          type="text"
          placeholder={course.category}
          required
        /></li>
        <li><label>About</label>
        <input
          {...overview}
          type="text"
          placeholder={course.overview}
          required
        /></li>
        <li><label>Image</label>
        <input {...image} type="text" placeholder={course.image} required/></li>
        <li><label>Duration</label>
        <input {...duration} type="text" placeholder={course.duration} required /></li>
        <li> <label>Requirements</label>
        <input
          {...requirements}
          type="text"
          placeholder={course.requirements}
          required
        /></li>
        <button>UPDATE</button>
        </ul>
      </form>

      <div className="containerSingle">
     <div className="singleCardRegister">
        <div className="cardTitle">
        <form onSubmit={handlerSubmit}>
              <legend>
                <h1>Register</h1>
              </legend>
          <div className="mb-3">
            <label>User Name</label>
            <input
              {...userName}
              type="text"
              placeholder="User Name"
              required
              minlength="3"
              maxlength="20" 
              className="form-control"
            />
          </div>
          <div className="mb-3">
            <label>Name and lastname</label>
            <input
              {...nameAndLastname}
              type="text"
              placeholder="Name and lastname"
              className="form-control"
              required
            />
          </div>
          <div className="mb-3">
            <label>Email</label>
            <input
              {...email}
              type="email"
              placeholder="email@futurum.com"
              className="form-control"
              required
            />
          </div>
          <label>City</label>
          <input {...city} type="text" placeholder="City" required className="form-control"/>
          <label>Country</label>
          <input {...country} type="text" placeholder="Country" required className="form-control" />
          <label>Password</label>
          <input
            {...password}
            type="password"
            placeholder=">Password"
            className="form-control"
            required
            minlength="6"
            maxlength="8"
          />
          <div className="btnAddR"> 
            <button type="submit" className="btn btn-dark btn-lg btnAdd" >Register</button>
          </div>
        </form>
    </div>
  </div>
</div>

    </div>



  );


};


export default EditCourse;
